function ColorComponent (props) {
    const [message, setMessage] = useState();

    setMessage( props.bored ? '놀러 가자' : '하던 일 열심히 마무리하기')


    return (
        <div style={{ color: props.color }}>
            안녕하세요 {props.name}

            {message}
        </div>
    );
}

export default ColorComponent;
